(273, 83, '_form', '<div class="row">\n                                    <div class="col-lg-6 col-md-6 col-sm-6 col-xs-12 col-tn-12">\n                                        <div class="form-group">\n                                 [text your-name class:form-control placeholder "Họ tên "]\n                                           \n                                        </div>\n                                    </div>\n                                    <div class="col-lg-6 col-md-6 col-sm-6 col-xs-12 col-tn-12">\n                                        <div class="form-group">\n        [text your-subject class:form-control placeholder "Tiêu đề "]\n                                        </div>\n                                    </div>\n                                </div>\n                                <div class="row">\n                                    <div class="col-lg-6 col-md-6 col-sm-6 col-xs-12 col-tn-12">\n                                        <div class="form-group">\n                     [text* your-email class:form-control placeholder "Email"]\n                                        </div>\n                                        <div class="form-group">\n                 [text* your-phone class:form-control placeholder "Số điện thoại"]\n                                        </div>\n                                        <div class="form-group">\n                       [text* your-adress class:form-control placeholder "Địa chỉ"]\n                                        </div>\n                                    </div>\n                                    <div class="col-lg-6 col-md-6 col-sm-6 col-xs-12 col-tn-12">\n                                        <div class="form-group">\n [textarea your-message class:form-control placeholder "Nội dung"] \n\n                                        </div>\n                                    </div>\n                                </div>\n                                <div class="row">\n                                    <div class="col-lg-3 col-md-3 col-sm-3 col-xs-4 col-tn-4">\n                                        <div class="form-group text_baomat">\n                                            Mã bảo mật\n                                        </div>\n                                    </div>\n                                    <div class="col-lg-3 col-md-3 col-sm-3 col-xs-4 col-tn-4">\n                                        <div class="form-group">\n                                         \n[captchar your-captcha class:form-control ]\n                                        </div>\n                                    </div>\n                                    <div class="col-lg-3 col-md-3 col-sm-3 col-xs-4 col-tn-4">\n                                        <div class="form-group">\n                                            [captchac your-captcha size:s fg:#ffffff bg:#000000]\n                                        </div>\n                                    </div>\n                                    <div class="col-lg-3 col-md-3 col-sm-3 col-xs-12 col-tn-12">\n                                        <div class="form-group">\n                                            <button class="btn btn_site"> gửi</button>\n                                        </div>\n                                    </div>\n                                </div>'),
(274, 83, '_mail', 'a:9:{s:6:"active";b:1;s:7:"subject";s:45:"Xương khớp Nhất Nhất "[your-subject]"";s:6:"sender";s:33:"[your-name] <wordpress@tonka.dev>";s:9:"recipient";s:20:"hoangtd090@gmail.com";s:4:"body";s:183:"From: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on Xương khớp Nhất Nhất (http://tonka.dev)";s:18:"additional_headers";s:22:"Reply-To: [your-email]";s:11:"attachments";s:0:"";s:8:"use_html";b:0;s:13:"exclude_blank";b:0;}'),
